import React, { useState, useEffect } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"

function BookingPage() {
  const { carId } = useParams();
  const [selectedTimeSlot, setSelectedTimeSlot] = useState("");
  const [allResponse, setAllResponse] = useState([]); 
  const [timeSlots, setTimeSlots] = useState([]); 

  console.log("TS: ",timeSlots)
  console.log("Car ID: ",carId)


  useEffect(() => {
    axios
      .get(`http://localhost:3000/booking/${carId}`)
      .then((response) => {
        // Assuming response.data contains the booking data for the specific car
        const bookingData = response.data;
  
        // Get the carId from the booking data
        const bookingCarId = bookingData.carId;
  
        // Find the matching car entry in the timeSlots data
        const matchingCarEntry = timeSlots.find((entry) => entry.carId === bookingCarId);
  
        if (matchingCarEntry) {
          // You can now access the start and end timeSlots
          const startTimeSlot = matchingCarEntry.startTime;
          const endTimeSlot = matchingCarEntry.endTime;
  
          // Do something with the start and end timeSlots
          console.log("Start Time Slot:", startTimeSlot);
          console.log("End Time Slot:", endTimeSlot);
        }
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [carId, timeSlots]);
  
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/cars")
      .then((response) => {
        setTimeSlots(response.data);
      })
      .catch((error) => {
        console.error("Error fetching car data:", error);
      });
  }, []);
  



  // useEffect(() => {
