import React, { useState } from 'react';
import CarList from './components/CarList';
import BookingForm from './components/BookingForm';
import CarDetails from './components/CarDetails';
import './App.css';

function App() {
  const [cars, setCars] = useState([
    { id: 1, name: 'Car 1', timeSlots: ['1 pm - 2 pm', '3 pm - 4 pm'] },
    { id: 2, name: 'Car 2', timeSlots: ['2 pm - 3 pm', '4 pm - 5 pm'] },
    // Add more cars here
  ]);

  const [selectedCar, setSelectedCar] = useState(null);

  const handleBookCar = (carId, timeSlot) => {
    // Find the selected car in the cars array
    const selectedCarIndex = cars.findIndex((car) => car.id === carId);

    if (selectedCarIndex === -1) {
      // Car not found, handle error or display a message
      console.error('Car not found');
      return;
    }

    // Create a copy of the cars array to avoid mutating state directly
    const updatedCars = [...cars];

    // Find the selected time slot in the car's timeSlots array
    const car = updatedCars[selectedCarIndex];
    const timeSlotIndex = car.timeSlots.indexOf(timeSlot);

    if (timeSlotIndex === -1) {
      // Time slot not found, handle error or display a message
      console.error('Time slot not found');
      return;
    }

    // Mark the time slot as booked (e.g., replace it with 'Booked')
    car.timeSlots[timeSlotIndex] = 'Booked';

    // Update the state with the modified cars array
    setCars(updatedCars);
  };  

  return (
    <div className="App">
      <h1>Car Rental Website</h1>
      <CarList cars={cars} setSelectedCar={setSelectedCar} />
      <BookingForm cars={cars} onBook={handleBookCar} />
      {selectedCar && <CarDetails car={selectedCar} />}
    </div>
  );
}

export default App;
