import React, { useState, useEffect } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"

function BookingPage() {
  const { carId } = useParams()
  const [selectedTimeSlot, setSelectedTimeSlot] = useState("")
  const [timeSlots, setTimeSlots] = useState([])
  //console.log("Timeslot ",timeSlots)
  console.log("Car ID", carId)

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/cars/${carId}`)
      .then((response) => {
        console.log("TimeSlots: ",response.data.timeSlots)
        setTimeSlots(response.data.timeSlots);
      })
      .catch((error) => {
        console.error("Error fetching time slots:", error);
      });
  }, [carId]);  

  const handleBooking = () => {
    if (selectedTimeSlot) {
      axios
        .post(`http://localhost:5000/api/cars/${carId}/book`, {
          timeSlot: selectedTimeSlot,
        })
        .then((response) => {
          alert("Booking successful!")
        })
        .catch((error) => {
          console.error("Error booking car:", error)
        })
    } else {
      alert("Please select a time slot.")
    }
  }

  return (
    <div>
      <h1>Book a Car</h1>
      <label>Select a Time Slot:</label>
      <select
        value={selectedTimeSlot}
        onChange={(e) => setSelectedTimeSlot(e.target.value)}
      >
        <option value="">Select a time slot</option>
        {timeSlots.map((timeSlot) => (
          <option key={timeSlot.id} value={timeSlot.id}>
            {timeSlot.startTime} - {timeSlot.endTime}
          </option>
        ))}
      </select>

      <button onClick={handleBooking}>Book Car</button>
    </div>
  )
}

export default BookingPage
